import PropTypes from "prop-types";
import * as SearchAPI from "./SearchAPI";
import React from "react";

const {Component} = require("react");

class SearchNav extends Component {
    state = {
        response: undefined,
        currentPage: 1,
        query: "",
        pageSize: 18,
    }

    componentDidMount() {
        SearchAPI.getResponse(this.state.query, this.state.currentPage, this.state.pageSize)
            .then(r => {
                this.setState({ response: r});
            });
    }

    componentDidUpdate() {
        SearchAPI.getResponse(this.state.query, this.state.currentPage, this.state.pageSize)
            .then(r => {
                this.setState({ response: r});
            });
    }

    incrementPage() {
        const newPage = this.state.currentPage + 1;
        this.setState({ currentPage: newPage })
    }

    decrementPage() {
        const newPage = this.state.currentPage - 1;
        this.setState({ currentPage: newPage })
    }


    render () {
        const response = this.state.response;
        const currentPage = this.state.currentPage;
        if (response === undefined) {
            return (<button>Loading</button>);
        } else if(response.data.pagination.last === currentPage) {
            return (
                <React.Fragment>
                    <button> 1 </button>
                    <button> &lh </button>
                    <button> ${currentPage} </button>
                </React.Fragment>
            )
        } else if (currentPage === 1) {
            return (
                <React.Fragment>
                    <button disabled> 1 </button>
                    <button> > </button>
                    <button> {response.data.pagination.last} </button>
                </React.Fragment>
            )
        }

        return (<button>Click me to see response</button>);
    }
}

export default SearchNav;

SearchNav.propTypes = {
    page: PropTypes.number,
    totalPages: PropTypes.number,
};
