import axios from "axios";
require("dotenv").config(); //Env file reader
const JHED = process.env.JHED;// || "barcher9";
const URL = process.env.BASE_URL;// || "https://courses-api-hw4.herokuapp.com";
console.log(JHED, URL);
const headers = {
    Authorization: JHED,
};

async function getAll() {
    try {
        const response = await axios.get(`${BASE_URL}/api/courses`, { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return [];
    }
}

async function remove(id) {
    try {
        const response = await axios.delete(`${BASE_URL}/api/courses/${id}`, { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return null;
    }
}

async function add(course) {
    try {
        const response = await axios.post(`${BASE_URL}/api/courses`,
                course,
            { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return null;
    }
}

async function updateStatus(id, status) {
    const data = {
        status: status
    };
    try {
        const response = await axios.patch(`${BASE_URL}/api/courses/${id}`,
            data,
            { headers });
        return response.data.data;
    } catch (err) {
        console.log(err.response);
        return null;
    }
}

export { getAll, remove, add, updateStatus };
