import React, { Component } from "react";
import { Route, Switch } from "react-router";
import "./App.css";
import * as CoursesAPI from "./CoursesAPI.js";
import SearchPage from "./Search";
import { Link } from "react-router-dom";
import CourseCategory from "./CourseCategory";

const sample = [
  {
    "status": "taken",
    "title": "Intermediate Programming",
    "number": "601.220",
    "term": "Spring 2020"
  },
  {
    "status": "interested",
    "title": "Object Oriented Software Engineering",
    "number": "601.421",
    "term": "Spring 2021"
  },
  {
    "status": "taken",
    "title": "Data Structures",
    "number": "601.226",
    "term": "Spring 2020"
  },
  {
    "status": "enrolled",
    "title": "Full-Stack JavaScript",
    "number": "601.280",
    "term": "Fall 2020"
  },
  {
    "status": "taken",
    "title": "Gateway Computing: JAVA",
    "number": "500.112",
    "term": "Fall 2019"
  }
];
/*sample.forEach((course) => {
  CoursesAPI.add(course).then(r => console.log(r));
});*/

class CourseApp extends Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     *  we're on, use the URL in the browser's address bar (i.e. react-router)
     */
    showSearchPage: false,
    courses: [
    ],
    color: "#a05dac",
    statuses: ["taken", "enrolled", "interested"],
    reload: false,
  };

  onCourseUpdate = (course, status) => {
    const { _id } = course;
    if (status === "none") {
      this.removeCourse(course);
      return;
    }
    const data = {
      status: status
    };
    CoursesAPI.updateStatus(_id, status).then(r => {
      const course = this.state.courses.forEach(c => {
        if (c._id === _id) {
          c.status = status;
          return c;
        }
        return c;
      });
      this.setState({ courses: course});
    });
  }

  addCourse = (course) => {
    CoursesAPI.add(course).then((c) => {
      if (c === null) {
        alert("Error adding Course");
        return;
      }
      this.setState({
        courses: this.state.courses.concat(c)
      });
    });


  };

  removeCourse = (course) => {
    this.setState((state) => {
      return {
        courses: state.courses.filter((c) => c._id !== course._id),
      };
    });
    CoursesAPI.remove(course._id).then(r => {
      if (r === null) {
        alert("Error removing course");
      }
    });
  };

  componentDidMount() {
    CoursesAPI.getAll().then((courses) => this.setState({ courses }));
  }

  render() {
    const { courses, color, statuses}  = this.state;
    return (
      /**
       * TODO: Refactor this code to reusable React components
       */
      <div className="app">
        {
          <Switch>
            <Route exact path="/">
              <div className="list-courses">
                <div className="list-courses-title">
                  <h1>My Courses</h1>
                </div>
                <div className="list-courses-content">
                  <div>
                    {
                      statuses.map(
                          (status, index) => {
                            return (
                                <CourseCategory
                                    courses={courses}
                                    color={color}
                                    status={status}
                                    onCourseUpdate={this.onCourseUpdate}
                                    key={index}/>);
                          }
                      )
                    }
                  </div>
                </div>
                <div className="open-search">
                  <Link to="/search">
                    <button/>
                  </Link>
                </div>
              </div>
            </Route>
            <Route exact path="/search">
                <SearchPage onCourseUpdate={this.onCourseUpdate}/>
            </Route>
          </Switch>
        }
      </div>
    );
  }
}

export default CourseApp;
