import axios from "axios";

const JHED = process.env.JHED || "barcher9";
const BASE_URL = process.env.BASE_URL || "https://courses-api-hw4.herokuapp.com";
const headers = {
    Authorization: JHED,
};

async function getAll() {
    try {
        const response = await axios.get(`${BASE_URL}/api/courses`, { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return [];
    }
}

async function remove(id) {
    try {
        const response = await axios.delete(`${BASE_URL}/api/courses/${id}`, { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return null;
    }
}

async function add(course) {
    try {
        const response = await axios.post(`${BASE_URL}/api/courses`,
                course,
            {headers});
        return response.data.data;
    } catch (err) {
        console.log(err);
        return null;
    }
}

async function updateStatus(course) {
    try {
        const response = await axios.patch(`${BASE_URL}/api/courses`,
            {headers, course});
        return response.data.data;
    } catch (err) {
        console.log(err);
        return null;
    }
}

export { getAll, remove, add };
