import { Component } from "react";
import "./App.css";

function CourseCard(props) {
  const { course, color } = props;
  return (<div className="course">
    <div className="course-top">
      <svg className="course-logo">
        <rect
            x={0}
            y={0}
            width={128}
            height={128}
            fill={color}
        />
        <text
            x="50%"
            y="35%"
            textAnchor="middle"
            dominantBaseline="middle"
            fill="white"
            fontFamily="monospace"
            fontSize="2.2em"
        >
          <tspan x="50%" dy="0">
            {course.number.split(".")[0]}
          </tspan>
          <tspan x="50%" dy="1.2em">
            {course.number.split(".")[1]}
          </tspan>
        </text>
      </svg>
      <div className="course-status-changer">
        <select value={course.status}>
          <option value="move" disabled>
            Move to...
          </option>
          <option value="enrolled">
            Currently Enrolled
          </option>
          <option value="interested">Want to Take</option>
          <option value="taken">Already Took</option>
          <option value="none">None</option>
        </select>
      </div>
    </div>
    <div className="course-title">
      {course.title}
    </div>
    <div className="course-term">{course.term}</div>
  </div>);
}

function CourseCategory(props) {
  const {courses, color} = props;
  return (<div className="dashboard">
    <h2 className="dashboard-title">Already Took</h2>
    <div className="dashboard-courses">
      <ol className="courses-grid">
        {
          courses.map(
              (course, index) => {
                return <li key={index}>
                  <CourseCard course={course} color={color}/>
                </li>
              }
          )
        }
      </ol>
    </div>
  </div>);
}


class CourseApp extends Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     *  we're on, use the URL in the browser's address bar (i.e. react-router)
     */
    showSearchPage: false,
    courses: [
      {
        title: "Gateway Computing: Java",
        number: "500.112",
        term: "Fall 2019",
        status: "taken",
      },
      {
        title: "Intermediate Programming",
        number: "601.220",
        term: "Spring 2020",
        status: "taken",
      },
      {
        title: "Data Structures",
        number: "601.226",
        term: "Spring 2020",
        status: "taken",
      },
      {
        title: "Full-Stack JavaScript",
        number: "601.280",
        term: "Fall 2020",
        status: "enrolled",
      },
      {
        title: "Object-Oriented Software Engineering",
        number: "601.421",
        term: "Spring 2021",
        status: "interested",
      },
    ],
    color: "#a05dac",
    status: ["taken", "enrolled", "interested"],
  };

  render() {
    const { courses, color}  = this.state;
    return (
      /**
       * TODO: Refactor this code to reusable React components
       */
      <div className="app">
        {
          <div className="list-courses">
            <div className="list-courses-title">
              <h1>My Courses</h1>
            </div>
            <div className="list-courses-content">
              <div>
                <CourseCategory courses={courses} color={color}/>
              </div>
            </div>
            <div className="open-search">
              <button onClick={() => this.setState({ showSearchPage: true })}>
                Add a course
              </button>
            </div>
          </div>
        }
      </div>
    );
  }
}

export default CourseApp;
