import axios from "axios";

const JHED = process.env.JHED || "barcher9";
const BASE_URL = process.env.BASE_URL || "https://courses-api-hw4.herokuapp.com";
const headers = {
    Authorization: JHED,
};

async function getDataAtPage(query, page, pageSize) {
    if (query === null) {
        query = "";
    }
    if (page === null) {
        page = 0;
    }
    if (pageSize === null) {
        pageSize = 10;
    }
    try {
        const response = await axios.get(`${BASE_URL}/api/search?query=${query}&page=${page}&limit=${pageSize}`, { headers });
        return response.data.data;
    } catch (err) {
        console.log(err);
        return [];
    }
}

export { getDataAtPage };
