import React, {Component} from "react";
import * as CoursesAPI from "./CoursesAPI";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import CourseCategory from "./CourseCategory";
import "./SearchAPI"
import CourseCard from "./CourseCard";
import * as SearchAPI from "./SearchAPI";

class SearchPage extends Component {
    state = {
        courses: [],
    }

    addCourseFromSearch (course, status) {
        course.status = status;
        CoursesAPI.add(course).then(r => console.log(r));
    }

    componentDidMount() {
        SearchAPI.getDataAtPage("", 1, 10)
            .then(r => {
                const page = r.forEach((c) => {
                    c.status = "none";
                    return c;
                });
                this.setState({ courses: page});
            });
    }

    render() {
        const courses = this.state.courses;
        console.log("This is courses ->",courses);
        const color = "#a05dac";
        return (
            <div className="search-courses">
                <div className="search-courses-bar">
                    <Link to="/">
                        <button className="close-search" />
                    </Link>
                    <div className="search-courses-input-wrapper">
                        <input
                            type="text"
                            placeholder="Search by course title or course number"
                        />
                    </div>
                </div>
                <div className="search-courses-results">
                    <ol className="courses-grid">
                        {
                            courses.map(
                                (course, index) => {
                                    return <li key={index}>
                                        <CourseCard course={course} color={color} props={this.props} onCourseUpdate={this.props.onCourseUpdate}/>
                                    </li>
                                }
                            )
                        }
                    </ol>
                </div>
            </div>
        );
    }
}

export default SearchPage;

SearchPage.propTypes = {
    courses: PropTypes.arrayOf (
        PropTypes.shape({
            _id: PropTypes.string,
            term: PropTypes.string,
            title: PropTypes.string,
            number: PropTypes.string,
            status: PropTypes.string
        })
    ).isRequired,
    onCourseUpdate: PropTypes.func.isRequired,
};
