import React, {Component} from "react";
import PropTypes from "prop-types";
import CourseControl from "./CourseControl";

class CourseCard extends Component {
    render() {
        const { course } = this.props;
        const color = "#a05dac";
        return (<div className="course">
            <div className="course-top">
                <svg className="course-logo">
                    <rect
                        x={0}
                        y={0}
                        width={128}
                        height={128}
                        fill={color}
                    />
                    <text
                        x="50%"
                        y="35%"
                        textAnchor="middle"
                        dominantBaseline="middle"
                        fill="white"
                        fontFamily="monospace"
                        fontSize="2.2em"
                    >
                        <tspan x="50%" dy="0">
                            {course.number.split(".")[0]}
                        </tspan>
                        <tspan x="50%" dy="1.2em">
                            {course.number.split(".")[1]}
                        </tspan>
                    </text>
                </svg>
                <div className="course-status-changer">
                    <CourseControl course={course} props={this.props} onCourseUpdate={this.props.onCourseUpdate}/>
                </div>
            </div>
            <div className="course-title">
                {course.title}
            </div>
            <div className="course-term">{course.term}</div>
        </div>);
    }
}

export default CourseCard;

CourseCard.propTypes = {
    course: PropTypes.shape({
            _id: PropTypes.string,
            term: PropTypes.string,
            title: PropTypes.string,
            number: PropTypes.string,
            status: PropTypes.string
        }).isRequired,
    onCourseUpdate: PropTypes.func.isRequired,
};
