import PropTypes from "prop-types";
import * as SearchAPI from "./SearchAPI";
import React from "react";

const {Component} = require("react");

class SearchNav extends Component {
    state = {
        response: undefined,
        currentPage: 1,
        query: "",
        pageSize: 18,
        leftArrow: "<",
        canUpdate: false,
    }

    /*componentDidMount() {
        SearchAPI.getResponse(this.state.query, this.state.currentPage, this.state.pageSize)
            .then(r => {
                this.setState({ response: r});
            });
    }

    componentDidUpdate() {
        if (this.state.canUpdate) {
            SearchAPI.getResponse(this.state.query, this.state.currentPage, this.state.pageSize)
                .then(r => {
                    this.setState({response: r, canUpdate: false});
                });
        }
    }*/

    incrementPage() {
        /*const newPage = this.state.currentPage + 1;
        this.setState({ currentPage: newPage,
        canUpdate: true})*/
        this.props.incrementPage();
    }

    decrementPage() {
        /*const newPage = this.state.currentPage - 1;
        this.setState({ currentPage: newPage,
        canUpdate: true})*/
        this.props.decrementPage();
    }

    setLastPage () {
        //this.setState({ currentPage: this.state.response.data.pagination.last})
        this.props.setLastPage();
    }

    setFirstPage () {
        //this.setState({ currentPage: 1})
        this.props.setFirstPage();
    }


    render () {
        const response = this.props.response;
        const currentPage = this.props.currentPage;
        if (response === undefined) {
            return (<button>Loading</button>);
        } else if(response.data.pagination.last === currentPage) {
            return (
                <React.Fragment>
                    <button onClick={this.setFirstPage.bind(this)}> 1 </button>
                    <button onClick={this.decrementPage.bind(this)}> {this.state.leftArrow} </button>
                    <button disabled> {currentPage} </button>
                </React.Fragment>
            )
        } else if (currentPage === 1) {
            return (
                <React.Fragment>
                    <button disabled> 1 </button>
                    <button onClick={this.incrementPage.bind(this)}> > </button>
                    <button onClick={this.setLastPage.bind(this)}> {response.data.pagination.last} </button>
                </React.Fragment>
            )
        } else if (currentPage === 2) {
            return (
            <React.Fragment>
                <button onClick={this.setFirstPage.bind(this)}> 1 </button>
                <button disabled> 2 </button>
                <button onClick={this.incrementPage.bind(this)}> > </button>
                <button onClick={this.setLastPage.bind(this)}> {response.data.pagination.last} </button>
            </React.Fragment>
            )
        }
        return (
        <React.Fragment>
            <button onClick={this.setFirstPage.bind(this)}> 1 </button>
            <button onClick={this.decrementPage.bind(this)}> {this.state.leftArrow} </button>
            <button disabled> {currentPage} </button>
            <button onClick={this.incrementPage.bind(this)}> > </button>
            <button onClick={this.setLastPage.bind(this)}> {response.data.pagination.last} </button>
        </React.Fragment>);
    }
}

export default SearchNav;

SearchNav.propTypes = {
    response: PropTypes.array,
    currentPage: PropTypes.number,
    incrementPage: PropTypes.func.isRequired,
    decrementPage: PropTypes.func.isRequired,
    setFirstPage: PropTypes.func.isRequired,
    setLastPage: PropTypes.func.isRequired
};
