import React, {Component} from "react";
import * as CoursesAPI from "./CoursesAPI";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import "./SearchAPI"
import CourseCard from "./CourseCard";
import * as SearchAPI from "./SearchAPI";
import SearchNav from "./SearchNav";

class SearchPage extends Component {
    state = {
        courses: [],
        currentPage: 1,
        response: undefined,
        query: "",
        pageSize: 18,
    }

    addCourseFromSearch (course, status) {
        course.status = status;
        CoursesAPI.add(course).then(r => console.log(r));
    }

    componentDidMount() {
        SearchAPI.getResponse(this.state.query, 1, 18)
            .then(r => {
                this.setState({ response: r, courses: r.data.data});
            });
    }

    updateQuery = () => {
        const value = document.getElementById("searchBar").value;
        this.setState({ query: value});
    }

    onCourseUpdate = (course, status) =>  {
        course.status = status;
        CoursesAPI.add(course).then(r => {
            if (r === null) {
                alert("There was an error adding this course, contact the system administrator");
                return;
            }
            this.props.onCourseUpdate(r, status);
        });
    }

    incrementPage = () => {
        const newPage = this.state.currentPage + 1;
        this.setState({ currentPage: newPage,
            canUpdate: true})
    }

    decrementPage = () => {
        const newPage = this.state.currentPage - 1;
        this.setState({ currentPage: newPage,
            canUpdate: true})
    }

    setLastPage = () => {
        this.setState({ currentPage: this.state.response.data.pagination.last})
    }

    setFirstPage = () => {
        this.setState({ currentPage: 1})
    }

    render() {
        const courses = this.state.courses;
        const color = "#a05dac";
        return (
            <div className="search-courses">
                <div className="search-courses-bar">
                    <Link to="/">
                        <button className="close-search" />
                    </Link>
                    <div className="search-courses-input-wrapper">
                        <input
                            id="searchBar"
                            type="text"
                            placeholder="Search by course title or course number"
                            onChange={this.updateQuery.bind(this)}
                        />
                    </div>
                </div>
                <div className="search-courses-results">
                    <ol className="courses-grid">
                        {
                            courses.map(
                                (course, index) => {
                                    course.status = "none";
                                    return <li key={index}>
                                        <CourseCard course={course} color={color} props={this.props} onCourseUpdate={this.onCourseUpdate}/>
                                    </li>
                                }
                            )
                        }
                    </ol>
                    <div className="search-courses-buttons">
                        <SearchNav response={this.state.response}
                        incrementPage={this.incrementPage}
                        decrementPage={this.decrementPage}
                        currentPage={this.state.currentPage}
                        setFirstPage={this.setFirstPage}
                        setLastPage={this.setLastPage}/>
                    </div>
                </div>
            </div>
        );
    }
}

export default SearchPage;

SearchPage.propTypes = {
    onCourseUpdate: PropTypes.func.isRequired,
};
