import React, {Component} from "react";
import PropTypes from "prop-types";
import CourseCard from "./CourseCard";
import Color from "./Color";


const statusMap = new Map([["enrolled", "Currently Enrolled"], ["taken", "Already Took"], ["interested", "Want to Take"]]);
class CourseCategory extends Component {
    state = {
        statuses: ["taken", "enrolled", "interested"],
    }

    render() {
        const { status } = this.props;
        const courses = this.props.courses.filter(
            (course) => {
                return course.status === status;
            }
        );
        return (<div className="dashboard">
            <h2 className="dashboard-title">{statusMap.get(status)}</h2>
            <div className="dashboard-courses">
                <ol className="courses-grid">
                    {
                        courses.map(
                            (course, index) => {
                                return (<li key={index}>
                                    <CourseCard course={course} color={Color.getColor(course.title)} props={this.props} onCourseUpdate={this.props.onCourseUpdate}/>
                                </li>);
                            }
                        )
                    }
                </ol>
            </div>
        </div>);
    }
}

export default CourseCategory;

CourseCategory.propTypes = {
    courses: PropTypes.arrayOf (
        PropTypes.shape({
            _id: PropTypes.string,
            term: PropTypes.string,
            title: PropTypes.string,
            number: PropTypes.string,
            status: PropTypes.string
        })
    ).isRequired,
    onCourseUpdate: PropTypes.func.isRequired,
};
