import React, {Component} from "react";
import {Link} from "react-router-dom";
import PropTypes from "prop-types";
import "./SearchAPI"
import CourseCard from "./CourseCard";
import * as SearchAPI from "./SearchAPI";
import Pagination from "./Pagination";
import * as CoursesAPI from "./CoursesAPI";

class SearchPage extends Component {
    state = {
        courses: [],
        currentCourses: [],
        response: undefined,
    }

    componentDidMount() {
        this.setState({currentCourses: this.props.getCourses()});
    }

    updateQuery = () => {
        const value = document.getElementById("searchBar").value;
        if (value === "") {
            this.setState({courses: [], response: undefined});
            return;
        }
        SearchAPI.getResponse(value, 1, 10)
            .then(r => {
                const newCourses = this.newCoursesList(r.data.data);
                this.setState({ response: r, courses: newCourses});
            });
    }



    newCoursesList = (responseData) => {
        return responseData.map((course) => {
            course.status = "none";
            this.state.currentCourses.forEach((c) => {
                if (c.title === course.title && c.term === course.term && c.number === course.number) {
                    course.status = c.status;
                }
            });
            return course;
        });
    }


    onCourseUpdate = (course, status) =>  {
        let skip = false;
        CoursesAPI.getAll().then((courses) => {
            courses.every((c) => {
                if (c.title === course.title && c.term === course.term && c.number === course.number) {
                    if (status === "none") {
                        this.props.removeCourse(c._id);
                    } else {
                        this.props.onCourseUpdate(c._id, status);
                    }
                    skip = true;
                    return true;
                }
                return false;
            });
            if (!skip) {
                course.status = status;
                this.props.addCourse(course);
            }
            CoursesAPI.getAll().then((courses) => this.setState({ courses: courses }, () => {
                console.log("UPDATE");
                console.log(this.state.courses)
            }));
        });

       /* let skip = false;
        console.log("Current courses", this.state.currentCourses);
        this.setState({currentCourses: this.props.getCourses()}, () => {
            this.state.currentCourses.every((c) => {
                if (c.title === course.title && c.term === course.term && c.number === course.number) {
                    if(status === "none") {
                        this.props.removeCourse(c);
                    } else {
                        this.props.onCourseUpdate(c, status);
                    }
                    this.refresh();
                    skip = true;
                    return true;
                }
                return false;
            });
            if(!skip) {
                course.status = status;
                this.props.addCourse(course);
                this.refresh();
            }
        });*/

        /*if(course._id !== undefined) {
            this.state.currentCourses.every((c) => {
                if (c.title === course.title && c.term === course.term && c.number === course.number) {
                    this.updateView(course, status);
                    this.props.onCourseUpdate(c, status);
                    if (status === "none") {
                        this.setState((state) => {
                            return {
                                currentCourses: state.currentCourses.filter((tempCourse) => {
                                    return `${tempCourse._id}` !== c._id;
                                }),
                            };
                        });
                    }
                    return true;
                }
                return false;
            });
            return;
        }
        let newCourse = course;
        newCourse.status = status;
        CoursesAPI.add(newCourse).then(c => {
            if (c === null) {
                return;
            }
            this.setState({
                currentCourses: this.state.currentCourses.concat(c)
            }, () => {
                /!*this.updateView(course, status);
                this.props.onCourseUpdate(c, status);*!/
            });
        });*/
    }
    refresh = () => {
        this.setState({state: this.state});
    }

    updateView = (c, status) => {
        this.setState({ courses: this.state.courses.map((courses) => {
                if (c.title === courses.title && c.term === courses.term && c.number === courses.number) {
                    return {
                        title: courses.title,
                        term: courses.term,
                        number: courses.number,
                        status: status,
                    };
                }
                return courses;
            })});
    }

    nextPage = () => {
        SearchAPI.getResponseWithLink(this.state.response.data.links.next)
            .then(r => {
                const newCourses = this.newCoursesList(r.data.data);
                this.setState({ response: r, courses: newCourses});
            });
    }

    previousPage = () => {
        SearchAPI.getResponseWithLink(this.state.response.data.links.prev)
            .then(r => {
                const newCourses = this.newCoursesList(r.data.data);
                this.setState({ response: r, courses: newCourses});
            });
    }

    firstPage = () => {
        SearchAPI.getResponseWithLink(this.state.response.data.links.first)
            .then(r => {
                const newCourses = this.newCoursesList(r.data.data);
                this.setState({ response: r, courses: newCourses});
            });
    }

    lastPage = () => {
        SearchAPI.getResponseWithLink(this.state.response.data.links.last)
            .then(r => {
                const newCourses = this.newCoursesList(r.data.data);
                this.setState({ response: r, courses: newCourses});
            });
    }

    render() {
        const courses = this.state.courses;
        const color = "#a05dac";
        const response = this.state.response === undefined ? undefined: this.state.response.data;
        const pagination = response === undefined ? undefined: response.pagination;
        return (
            <div className="search-courses">
                <div className="search-courses-bar">
                    <Link to="/">
                        <button className="close-search" />
                    </Link>
                    <div className="search-courses-input-wrapper">
                        <input
                            id="searchBar"
                            type="text"
                            placeholder="Search by course title or course number"
                            onInput={this.updateQuery}
                        />
                    </div>
                </div>
                <div className="search-courses-results">
                    <ol className="courses-grid">
                        {
                            courses.map(
                                (course, index) => {
                                    return <li key={index}>
                                        <CourseCard
                                            course={course}
                                            color={color}
                                            props={this.props}
                                            onCourseUpdate={this.onCourseUpdate}
                                        />
                                    </li>
                                }
                            )
                        }
                    </ol>
                    <div className="search-courses-buttons">
                        <Pagination
                            pagination={pagination}
                            nextPage={this.nextPage}
                            previousPage={this.previousPage}
                            firstPage={this.firstPage}
                            lastPage={this.lastPage}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default SearchPage;

SearchPage.propTypes = {
    onCourseUpdate: PropTypes.func.isRequired,
    getCourses: PropTypes.func.isRequired,
    addCourse: PropTypes.func.isRequired,
    removeCourse: PropTypes.func.isRequired
};
