import React, { Component } from "react";
import "./App.css";
import axios from "axios";
import * as CoursesAPI from "./CoursesAPI.js";

require("dotenv").config({path:'../.env'});
const JHED = process.env.JHED || "barcher9";
const BASE_URL = process.env.BASE_URL || "https://courses-api-hw4.herokuapp.com";
const headers = {
  Authorization: JHED,
};

const sample = {
  term: "test",
  number: "601.220",
  status: "taken",
  title: "test"
};
const statusMap = new Map([["enrolled", "Currently Enrolled"], ["taken", "Already Took"], ["interested", "Want to Take"]]);

function CourseCard(props) {
  const { course, color } = props;
  return (<div className="course">
    <div className="course-top">
      <svg className="course-logo">
        <rect
            x={0}
            y={0}
            width={128}
            height={128}
            fill={color}
        />
        <text
            x="50%"
            y="35%"
            textAnchor="middle"
            dominantBaseline="middle"
            fill="white"
            fontFamily="monospace"
            fontSize="2.2em"
        >
          <tspan x="50%" dy="0">
            {course.number.split(".")[0]}
          </tspan>
          <tspan x="50%" dy="1.2em">
            {course.number.split(".")[1]}
          </tspan>
        </text>
      </svg>
      <div className="course-status-changer">
        <CourseControl course={course} props={props} onCourseUpdate={props.onCourseUpdate}/>
      </div>
    </div>
    <div className="course-title">
      {course.title}
    </div>
    <div className="course-term">{course.term}</div>
  </div>);
}

function CourseCategory(props) {
  const { status, color } = props;
  const courses = props.courses.filter(
      (course) => {
        return course.status === status;
      }
  );
  console.log(statusMap[status]);
  return (<div className="dashboard">
    <h2 className="dashboard-title">{statusMap[status]}</h2>
    <div className="dashboard-courses">
      <ol className="courses-grid">
        {
          courses.map(
              (course, index) => {
                return <li key={index}>
                  <CourseCard course={course} color={color} props={props} onCourseUpdate={props.onCourseUpdate}/>
                </li>
              }
          )
        }
      </ol>
    </div>
  </div>);
}

function CourseControl(props) {
  const { course } = props;
  return (<select value={course.status} onChange={ (e) => {
    props.onCourseUpdate(course, e.target.value);
  }}>
    <option value="move" disabled>
      Move to...
    </option>
    <option value="enrolled">
      Currently Enrolled
    </option>
    <option value="interested">Want to Take</option>
    <option value="taken">Already Took</option>
    <option value="none">None</option>
  </select>);
}

class CourseApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      /**
       * TODO: Instead of using this state variable to keep track of which page
       *  we're on, use the URL in the browser's address bar (i.e. react-router)
       */
      showSearchPage: false,
      courses: [
      ],
      color: "#a05dac",
      statuses: ["taken", "enrolled", "interested"],
      reload: false,
    };
  }

   onCourseUpdate = (course, status) => {
    const { _id } = course;
    if (status === "none") {
      this.removeCourse(course);
      return;
    }
    const data = {
      status: status
    };
    CoursesAPI.updateStatus(_id, status).then(r => {
      this.removeCourse(r);
      this.addCourse(r);
    });
  }

  addCourse = (course) => {
    CoursesAPI.add(course).then((c) => {
      if (c === null) {
        alert("Error adding Course");
        return;
      }
      this.setState({
        courses: this.state.courses.concat(c)
      });
    });


  };

  removeCourse = (course) => {
    this.setState((state) => {
      return {
        courses: state.courses.filter((c) => c._id !== course._id),
      };
    });
    CoursesAPI.remove(course._id).then(r => {
      if (r === null) {
        alert("Error removing course");
      }
    });
  };

  componentDidMount() {
    CoursesAPI.getAll().then((courses) => this.setState({ courses }));
  }

  render() {
    const { courses, color, statuses}  = this.state;
    return (
      /**
       * TODO: Refactor this code to reusable React components
       */
      <div className="app">
        {
          <div className="list-courses">
            <div className="list-courses-title">
              <h1>My Courses</h1>
            </div>
            <div className="list-courses-content">
              <div>
                {
                  statuses.map(
                      (status, index) => {
                        return (
                            <CourseCategory
                                courses={courses}
                                color={color}
                                status={status}
                                onCourseUpdate={this.onCourseUpdate}
                                key={index}/>);
                      }
                  )
                }
              </div>
            </div>
            <div className="open-search">
              <button onClick={() => this.setState({ showSearchPage: true })}>
                Add a course
              </button>
            </div>
          </div>
        }
      </div>
    );
  }
}

export default CourseApp;
