import axios from "axios";
require("dotenv").config({path: './.env'}); //Env file reader
const JHED = process.env.JHED;
const BASE_URL = process.env.BASE_URL;
console.log(JHED, BASE_URL);

const headers = {
    Authorization: JHED,
};

async function getResponse(query, page, pageSize) {
    try {
        return await axios.get(`${BASE_URL}/api/search?query=${query}&page=${page}&limit=${pageSize}`, { headers });
    } catch (err) {
        console.log(err);
        return [];
    }
}

async function getResponseWithLink(link) {
    try {
        return await axios.get(link, { headers });
    } catch (err) {
        console.log(err);
        return [];
    }
}


export { getResponse, getResponseWithLink };
