import axios from "axios";
require("dotenv").config(); //Env file reader
const JHED = process.env.REACT_APP_JHED || "barcher9";
const BASE_URL = process.env.REACT_APP_BASE_URL || "https://courses-api-hw4.herokuapp.com";
console.log(JHED, BASE_URL);

const headers = {
    Authorization: JHED,
};

async function getResponse(query, page, pageSize) {
    try {
        return await axios.get(`${BASE_URL}/api/search?query=${query}&page=${page}&limit=${pageSize}`, { headers });
    } catch (err) {
        console.log(err);
        return [];
    }
}

async function getResponseWithLink(link) {
    try {
        return await axios.get(link, { headers });
    } catch (err) {
        console.log(err);
        return [];
    }
}


export { getResponse, getResponseWithLink };
